#!/bin/bash

factorial() {
    num="$1"
    if [ "$num" -eq 0 ]; then
        echo 1
    else
        echo $((num * $(factorial $((num - 1)))))
    fi
}
num=5
echo "The factorial of $num is: $(factorial $num)"

#!/bin/bash

factorial() {
    num="$1"
    if [ "$num" -eq 0 ] || [ "$num" -eq 1 ]; then
        echo 1
    else
        echo $((num * $(factorial $((num - 1)))))
    fi
}
echo "Factorial of 5 is: $(factorial 5)"

#!/bin/bash

fibonacci() {
    n="$1"
    if [ "$n" -le 1 ]; then
        echo "$n"
    else
        echo $(( $(fibonacci $(($n - 1))) + $(fibonacci $(($n - 2))) ))
    fi
}
result=$(fibonacci 10)
echo "The 10th Fibonacci number is: $result"

